mapper.select:
SELECT * FROM star_movie
WHERE movie_title LIKE '%${keyword}%'
ORDER BY movie_viewer DESC LIMIT #{startRow}, #{listLimit}

mapper.insert:

	
	SELECT MAX(movie_idx) FROM movie

INSERT 
	INTO movie 
	VALUES (
		#{movie_idx} + 1
		,#{movie_title}
		,#{movie_grade}
		,#{movie_genre}
		,#{movie_open_date}
		,#{movie_runtime}
		,#{movie_intro}
		,#{movie_actors}
		,#{movie_picture}
		,#{movie_real_picture}
		,#{movie_teaser}
		,#{movie_viewer}
	)

mapper.update:
UPDATE movie
	SET
		movie_title = #{movie_title}
		, movie_grade = #{movie_grade}
		, movie_genre = #{movie_genre}
		, movie_open_date = #{movie_open_date}
		, movie_runtime = #{movie_runtime}
		, movie_intro = #{movie_intro}
		, movie_actors = #{movie_actors}
		, movie_picture = #{movie_picture}
		, movie_real_picture = #{movie_real_picture}
		, movie_teaser = #{movie_teaser}
		, movie_viewer = #{movie_viewer}
	WHERE
		movie_idx = #{movie_idx}

UPDATE movie
	SET movie_picture = null
	WHERE movie_idx = #{movie_idx}
