mapper.select:
SELECT * FROM star_movie
WHERE movie_title LIKE '%${keyword}%'
ORDER BY movie_viewer DESC LIMIT #{startRow}, #{listLimit}

SELECT m.movie_idx, movie_picture, movie_title, movie_open_date FROM movie m WHERE exists (SELECT * FROM movie v WHERE movie_open_date > curdate() AND m.movie_idx = v.movie_idx)
AND movie_title LIKE '%${keyword}%'
ORDER BY movie_idx LIMIT #{startRow}, #{listLimit}

mapper.delete:
DELETE 
	FROM movie_like 
	WHERE movie_idx = #{movie_idx} AND member_id = #{member_id}
